Data = ""

def DistinctSignal(_Input, _Length = 4):
    for Index in range(_Length-1, len(_Input)):
        InterimSet = set(_Input[Index - _Length + 1:Index+1])
        if len(InterimSet) == _Length:
            return Index + 1

print(f'Puzzle 1: Signal Start = {DistinctSignal(Data, 4)}')
print(f'Puzzle 2: Message Start = {DistinctSignal(Data, 14)}')